import React,{useState} from 'react'

export default function Aboutus() {
    const [mystyle,setmystyle] =useState({
        color:'white' ,
        backgroundColor:'black'
    })
    const [title,settitle]=useState('Enable dark mode')

    const togglemode = ()=>{
        if(mystyle.color === 'black'){
            setmystyle({
                color:'white',
                backgroundColor:'black'
            })
        }
        else{
            setmystyle({
                color:'black',
                backgroundColor:'white'
            })
        }
    }
    const changetitle = ()=>{
         if(mystyle.color === 'black'){
            settitle('En')
         }
    }
  return (
    <div className='container' style={mystyle}>
        <h1>About This Website</h1>
      <div class="accordion" id="accordionExample" style={mystyle}>
  <div class="accordion-item" style={mystyle}>
    <h2 class="accordion-header">
      <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne" style={mystyle}>
        Description
      </button>
    </h2>
    <div id="collapseOne" class="accordion-collapse collapse show" data-bs-parent="#accordionExample" >
      <div class="accordion-body" style={mystyle}>
        <strong>This website helps you to change and analyze your text as you wish.</strong>.For any technical support please mail us at abc123@gmail.com.
      </div>
    </div>
  </div>
  <div class="accordion-item" style={mystyle}>
    <h2 class="accordion-header">
      <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapseTwo" aria-expanded="false" aria-controls="collapseTwo" style={mystyle}>
        Developers
      </button>
    </h2>
    <div id="collapseTwo" class="accordion-collapse collapse" data-bs-parent="#accordionExample" style={mystyle}>
      <div class="accordion-body">
        <b>DHRUV GARG</b>
      </div>
    </div>
  </div>
</div>
<div className='container my-3' >
<button type="button" class="btn btn-primary" onClick={togglemode} onChange={changetitle}>{title}</button>
</div>

    </div>
  )
}
