    <div>
        <h1>{props.heading}</h1>
        <div class="mb-3">
           <textarea class="form-control" value={text} id="exampleFormControlTextarea1" rows="10"></textarea>
       </div>
       <button className='btn btn-primary'>Convert to uppercase</button>
    </div>
  )
}
