import React , {useState} from 'react'

export default function Textform(props) {
    const handleupclick = ()=>{
        let newtext=text.toUpperCase();
        setText(newtext);
    }
    const handleonchange = (event)=>{
        setText(event.target.value);
    }
    const handleloclick = ()=>{
        let newtext=text.toLowerCase();
        setText(newtext);
    }
    const [text,setText]=useState('enter text here')
  return (
    <>
    <div>
        <h1>{props.heading}</h1>
        <div class="mb-3">
           <textarea class="form-control" value={text} onChange={handleonchange} id="exampleFormControlTextarea1" rows="10"></textarea>
       </div>
       <button className='btn btn-primary mx-2' onClick={handleupclick}>Convert to uppercase</button>
       <button className='btn btn-primary mx-2' onClick={handleloclick}>Convert to uppercase</button>
    </div>
    <div>
        <h1>text analysis</h1>
        <p>Your text contains {text.split(' ').length()} words and </p>
    </div>
    </>
  )
}
